mapper.select:
SELECT sum(bail) as partBail ,sum(biAmount) as partBiAmount
,sum(rebackTotalAmount) as partBackBail from t_invest WHERE  customerId = #{customerId} and isDeleted = 0
and createTime between #{startTime} and #{endTime}

SELECT count(1) as partCount from t_invest WHERE  customerId = #{customerId} and isDeleted = 0
and createTime between #{startTime} and #{endTime}

  and status == #{status}

SELECT count(1) as contractTotal from t_contract WHERE  customerId = #{customerId} and isDeleted = 0

    and  createTime >= #{startTime}


    and  #{endTime} >= createTime

SELECT count(1) as partContractTotal,sum(totalAmount) as totalAmount from t_contract
WHERE  customerId = #{customerId} and isDeleted = 0
and financeType = #{financeType}

    and  createTime >= #{startTime}


    and  #{endTime} >= createTime

SELECT sum(payAmount) as payAmount  from ts_contract_record
WHERE  customerId = #{customerId} and isDeleted = 0
and payType = #{payType}

    and  payTime >= #{startTime}


    and  #{endTime} >= payTime
